import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { AiOutlineReload } from 'react-icons/ai';
import './TodoList.css';
import Todo from '../Todo/Todo';

const TodoList = () => {
    const [todoList, setTodoList] = useState([{ name: "You have no todos yet!" }]);
    const [refreshKey, setRefreshKey] = useState(0);
    const refreshList = () => {setRefreshKey(oldKey => oldKey + 1)}
    const getAllTodo = () => {
        axios('todo/', {
            method: "GET",
        }).then((res) => {
            setTodoList( [...res.data] );
        }).catch((err) => {
            console.log(err.message);
        });
    }
    useEffect(getAllTodo, [refreshKey]);
    console.log("Redering TodoList");
    return (
        <div className='todolist'>
            <div className='container'>
                <div className='heading'>{todoList.length} things todo</div>
                <button className='refresh-btn' onClick={refreshList}>Refresh <AiOutlineReload/></button>
            </div>
            <div className='todolist-list'>
                {todoList.map((item, index) => <Todo key={item._id} index={index+1} todo={item}/>)}
            </div>
        </div>
    );

}

export default TodoList;
