import React, { useState } from 'react';
import Login from '../Login/Login';
import Register from '../Register/Register';

import './Auth.css';

const Auth = () => {
    const [isRegistered, setIsRegistered] = useState(false);
    const [toggle, setToggle] = useState(false);
    return (
        <div className='auth'>
            {!toggle && <Register />}
            {toggle && <Login /> }
        </div>
    );
};

export default Auth;
