import React, { useState, useEffect } from 'react';
import axios from 'axios';

import './TodoList.css';
import Todo from '../Todo/Todo';

const getAllTodo = async () => {
    var todoList = [];
    await axios('todo/', {
        method: "GET",
    }).then((res) => {
        todoList = res.data;
    }).catch((err) => {
        console.log(err.message);
    });
    return todoList;
}

const TodoList = () => {
    let [todoList, setTodoList] = useState([{ name: "You have no todos yet!" }]);
    useEffect(()=>{
        fetch("http://localhost:9000/todo")
        .then(res => res.json())
        .then(data => setTodoList(data.message));
    },[]);
    console.log(todoList);
    return (
        <div className='todolist'>
            <div className='heading'>Things todo</div>
            {/* {todoList.map(item => <Todo name={item.name} />)} */}
            {/* {todoList.map(item => <Todo name={item.name} />)} */}
            {/* <Todo name={ todoList[0].name }/> */}
        </div>
    );

}

export default TodoList;
