import React, { useState, useEffect } from 'react';
import axios from 'axios';

import './TodoList.css';
import Todo from '../Todo/Todo';


const TodoList = () => {
    let [todoList, setTodoList] = useState([{ name: "You have no todos yet!" }]);

    useEffect(() => {
        axios('todo/', {
            method: "GET",
        }).then((res) => {
            // console.log([...res.data][0]._id);
            setTodoList( [...res.data] );
        }).catch((err) => {
            console.log(err.message);
        });
    }, []);

    return (
        <div className='todolist'>
            <div className='heading'>Things todo</div>
            {todoList.map(item => <Todo key={item._id} name={item.name} />)}
        </div>
    );

}

export default TodoList;
