import React from 'react';

import './TodoList.css';
import Todo from '../Todo/Todo';

import axios from 'axios';

const dummyTodos = [
    { name: "Go to the market" },
    { name: "Learn MERN stack" },
    { name: "Ride bike!" }];

function getAllTodo(){
    var todoList = [];
    axios('todo/', {
        method: "GET",
    }).then((res) => {
        todoList = res.data;
        // console.log(todoList);
    }).catch((err) => {
        console.log(err.message);
    });
    console.log(todoList);
    return todoList;
}

const TodoList = () => {
    var todoList = getAllTodo();
    
    return (
        <div className='todolist'>
            <div className='heading'>Things todo</div>
            {/* {todoList.map(item => <Todo name={item.name} />)} */}
            {/* <Todo name={ todoList[0].name }/> */}
        </div>
    );

}

export default TodoList;
