import React from 'react';
import axios from 'axios';

import './Todo.css';

const Todo = ({index, todo}) => {
    const name = todo.name;
    const _id = todo.id;
    var options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
    // const createdDate = todo.createdDate.toLocaleDateString("en-IN", options);
    const createdDate = todo.createdDate;
    const deleteTodo = () => {
        console.log("Deletign todo " + _id);
        const URL = "/todo/" + _id;
        console.log(URL);
        axios(URL, {
            method: "DELETE",
        }).then((res) => {
            alert("Deleted Todo: " + res.data.name);
        }).catch((err) => {
            console.log(err.message);
        });
    }

    return (
        <div className='todo'>
            <div className='todo-info'>
                <div className='todo-info-index'>{index}</div>
                <div>{name}</div>
                <div>{createdDate}</div>
            </div>
            <button className='todo-btn-del' onClick={deleteTodo}>Delete</button>
        </div>
    );
}

export default Todo;
