import React from 'react';
import axios from 'axios';

import './Todo.css';

const Todo = ({ _id, name }) => {

    const deleteTodo = () => {
        console.log("Deletign todo " + _id);
        const URL = "/todo/" + _id;
        console.log(URL);
        axios(URL, {
            method: "DELETE",
        }).then((res) => {
            alert("Deleted Todo: " + res.data.name);
        }).catch((err) => {
            console.log(err.message);
        });
    }

    return (
        <div className='todo'>
            <div>
                <div>1</div>
                <div>{name}</div>
            </div>
            <button className='todo-btn-del' onClick={deleteTodo}>Delete</button>
        </div>
    );
}

export default Todo;
