import React, { useState, useEffect } from 'react';
import axios from 'axios';

import './TodoList.css';
import Todo from '../Todo/Todo';
import getAllTodo from '../../controllers/getAllTodo';


const TodoList = () => {
    let [todoList, setTodoList] = useState([{ name: "You have no todos yet!" }]);

    useEffect( getAllTodo(setTodoList) , [] );

    return (
        <div className='todolist'>
            <div className='heading'>Things todo</div>
            {todoList.map(item => <Todo key={item._id} name={item.name} />)}
        </div>
    );

}

export default TodoList;
