import React from 'react';
import axios from 'axios';

import './TodoList.css';
import Todo from '../Todo/Todo';

const getAllTodo = async () => {
    var todoList = [];
    await axios('todo/', {
        method: "GET",
    }).then((res) => {
        todoList = res.data;
    }).catch((err) => {
        console.log(err.message);
    });
    return todoList;
}

const dummyTodos = [
    { name: "Go to the market" },
    { name: "Learn MERN stack" },
    { name: "Ride bike!" }];

const TodoList = () => {
    return (
        <div className='todolist'>
            <div className='heading'>Things todo</div>
            {/* {todoList.map(item => <Todo name={item.name} />)} */}
            {dummyTodos.map(item => <Todo name={item.name} />)}
            {/* <Todo name={ todoList[0].name }/> */}
        </div>
    );

}

export default TodoList;
