import React from "react";
import axios from "axios";

import "./Form.css";

const saveTodo = async (event) => {
    event.preventDefault();
    console.log("Clicked~");
    const URL = "/todo";
    const todoName = event.target.elements.name.value;
    if(todoName == "") return;
    console.log(todoName);
    await axios(URL, {
        method: "POST",
        data:{
            name: $(todoName),
        }
    }).then(res => {
        console.log(res);
    }).catch((err) => {
        console.log(err);
    });
};

function Form() {
    return (
        <form className="form" onSubmit={saveTodo}>
            <label className="label">What to do?</label>
            <input name="name" className="input" type="text"></input>
            <button className="button" type="submit">
                Add Todo
            </button>
        </form>
    );
}

export default Form;
