import axios from "axios";

/* To save one todo in database */

const saveTodo = async (event) => {
    event.preventDefault();
    console.log("Clicked~");
    const URL = "/todo";
    const todoName = event.target.elements.name.value;
    if(todoName == null || todoName == undefined || todoName === ""){
        alert("Please write What to do!!");
    }
    console.log(todoName);
    await axios(URL, {
        method: "POST",
        data:{
            name: todoName,
        }
    }).then(res => {
        console.log(res);
    }).catch((err) => {
        console.log(err);
    });
};

export default saveTodo;
